import React from 'react';
import {Audio, staticFile} from 'remotion';

interface VoiceoverTrackProps {
  marketCode: string;
  text: string;
}

export const VoiceoverTrack: React.FC<VoiceoverTrackProps> = ({marketCode, text}) => {
  // Try to load voiceover file if it exists
  // Files are generated by ElevenLabs API before rendering
  const voiceoverFile = `voiceover/${marketCode}-voiceover.mp3`;
  
  // Enable voiceover - will gracefully fail if file doesn't exist
  const hasVoiceover = true;
  
  if (!hasVoiceover) {
    return null;
  }
  
  // Remotion will handle missing files gracefully
  return (
    <Audio
      src={staticFile(voiceoverFile)}
      volume={0.8}
      startFrom={0}
    />
  );
};
